    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String emp_id = jLabel37.getText();
        String e_address = jTextField2.getText();
        String e_email = jTextField3.getText();
        String designation = jTextField6.getText();
        String emp_name = jTextField4.getText();
        String e_phone = jTextField5.getText();
        String branch_id = jTextField20.getText();
        try{
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/store","root","Quenton77Rebello");
            Statement st = con.createStatement();
            if((e_email.endsWith("@gmail.com") || e_email.endsWith("@hotmail.com")) && e_phone.length()==10)
            {
                st.executeUpdate("insert into employee values ('"+emp_id+"','"+emp_name+"','"+e_address+"','"+e_email+"','"+e_phone+"','"+designation+"','"+branch_id+"')");
                JOptionPane.showMessageDialog(null, "Succesfully inserted");
            }
            else
            {
              JOptionPane.showMessageDialog(null,"Incorrect Details ordered");      
            }
            
            
        }
        catch(Exception ex)
        {
            JOptionPane.showMessageDialog(null, ex);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

