    private void formComponentShown(java.awt.event.ComponentEvent evt) {//GEN-FIRST:event_formComponentShown
        // TODO add your handling code here:
        try{
            Connection con = DriverManager.getConnection("jdbc:mysql://localhost:3306/store","root","Quenton77Rebello");
            
            Statement st = con.createStatement(ResultSet.TYPE_SCROLL_SENSITIVE,ResultSet.CONCUR_READ_ONLY);
            ResultSet rs = st.executeQuery("select max(emp_id) from employee");
            if(rs.first())
            {
                String sid = rs.getString(1);
                int eid = Integer.valueOf(sid.substring(1));
                eid = eid + 1;
                String str = 'E' + String.valueOf(eid);
                jLabel37.setText(str);
            }
            else
                jLabel37.setText("E1");
            
            Statement st1 = con.createStatement(ResultSet.TYPE_SCROLL_SENSITIVE,ResultSet.CONCUR_READ_ONLY);
            ResultSet rs1 = st1.executeQuery("select max(mgr_id) from manager");
            if(rs1.first())
            {
                String sid1 = rs.getString(1);
                int id = Integer.valueOf(sid1.substring(1));
                id = id + 1;
                String str1 = 'M' + String.valueOf(id);
                jLabel38.setText(str1);
            }
            else
                jLabel38.setText("M1");
        }
        catch(Exception ex){
            JOptionPane.showMessageDialog(null, ex);
        } 
    }//GEN-LAST:event_formComponentShown

